FROM (
SELECT 
"stateId",
RANK() over (order by amtpp desc) as "rank"
FROM (SELECT "totalAmount" amtpp, "stateId" from  "StateSummary" ss join data.states s on s.id = ss."stateId" WHERE "year" = 'ALL' and "drugType" = 'ALL' and population is not null)a
)sm
WHERE id = sm."stateId";

