SET "rank" = sm."rank"
FROM (
SELECT 
"stateId",
"drugType",
RANK() over (partition by 
"drugType" order by "totalAmount" desc) as "rank"
FROM (
SELECT "totalAmount", "stateId", "drugType"
from  "StateSummary" ss
where "year" = 'ALL')a
)sm
WHERE ss."stateId" = sm."stateId" 
